/**
 * Editable link
 *
 * @copyright 2021 Adam Carlson - All rights reserved
 */

 import PropTypes from 'prop-types';
import './styles/editable_link.scss';
import React, {useState, memo} from 'react';
import EditLinkModal from './edit_link_modal';

function EditableLink({linkId, linkTitle, linkUrl, linkClicks, handleEditLink, handleDeleteLink}) {

  console.log("Rendering editable link");
  console.log(linkTitle);

   return (
    <li className="editable-link-item">
      <span className="editable-link-title">Link Title: {linkTitle}</span>
      <span className="editable-link-url">Link Url: {linkUrl}</span>
      <span className="editable-link-clicks">Clicks: {linkClicks}</span>
      <span className="edit-link-btn" onClick={() => {handleEditLink()}}>&#9998;</span>
      <span className="delete-link-btn" onClick={() => {handleDeleteLink()}}>&#x274C;</span>
    </li>
   );
 }

 /*
EditableLink.propTypes = {
  linkId: PropTypes.number.isRequired,
  linkTitle: PropTypes.string.isRequired,
  linkUrl: PropTypes.string.isRequired,
  linkClicks: PropTypes.number.isRequired,
  handleEditLink: PropTypes.func.isRequired,
  handleDeleteLink: PropTypes.func.isRequired
};
*/

 export default memo(EditableLink);
