/**
 * Modal to edit link
 *
 * @copyright 2021 Beacons - All rights reserved
 */

 import './styles/beacons_app.scss';
 import React, {useState, useEffect} from 'react';
 import {Modal, Button} from 'react-bootstrap';
 import PropTypes from 'prop-types';

function EditLinkModal(props) {
  const [linkTitle, setLinkTitle] = useState('');
  const [linkUrl, setLinkUrl] = useState('');

const handleLinkTitleChange = (event) => {
  const intputTitle = event.target.value;
  setLinkTitle(intputTitle);
};

const handleLinkUrlChange = (event) => {
  const intputUrl = event.target.value;
  setLinkUrl(intputUrl);
};

const handleSubmit = () => {
  const newUserLink = {
    title: linkTitle,
    url: linkUrl,
    clicks: 0
  }
  //props.setUserLinks(prevUserLinks => {
    //return [...prevUserLinks, newUserLink];
  //});
  //saveNewLink(newUserLink);
  //props.setShowNewLinkModal(false);
};

const saveNewLink = (newLink) => {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(newLink)
  };
  fetch('https://retoolapi.dev/lqtPSO/links', requestOptions);
}

  return (
    <Modal show={props.showEditLinkModal} onHide={() => props.setShowEditLinkModal(false)} animation={false}>
    <Modal.Header closeButton>
      <Modal.Title>Edit Post</Modal.Title>
    </Modal.Header>
    <Modal.Body>
    <input className="edit-link-title" type="text" value={props.currentLinkTitle} onChange={handleLinkTitleChange} />
     <input className="edit-link-url" type="text" value={props.currentLinkUrl} onChange={handleLinkUrlChange} />
     <button className="edit-link-btn" onClick={handleSubmit}>Edit Link</button>
    </Modal.Body>
  </Modal>
  );
}

EditLinkModal.propTypes = {
  showEditLinkModal: PropTypes.bool.isRequired,
  setShowEditLinkModal: PropTypes.func.isRequired,
  currentLinkTitle: PropTypes.string.isRequired,
  currentLinkUrl: PropTypes.string.isRequired,
  setUserLinks: PropTypes.func.isRequired
};

export default EditLinkModal;
