/**
 * Modal to update settings
 *
 * @copyright 2021 Beacons - All rights reserved
 */

 import './styles/beacons_app.scss';
 import React, {useState, useEffect} from 'react';
 import {Modal, Button} from 'react-bootstrap';
 import PropTypes from 'prop-types';

function SettingsModal(props) {
  const [inputLinkBackgroundColor, setInputLinkBackgroundColor] = useState(props.linkBackgroundColor);
  const [inputLinkColor, setInputLinkColor] = useState(props.linkColor);

const handleLinkBackgroundColor = (event) => {
  const intputLinkBackgroundColor = event.target.value;
  setInputLinkBackgroundColor(inputLinkBackgroundColor);
};

const handleLinkColor = (event) => {
  const intputLinkColor = event.target.value;
  setInputLinkColor(intputLinkColor);
};

const handleSubmit = () => {
  const newUserLink = {
    title: linkTitle,
    url: linkUrl,
    clicks: 0
  }
  props.setUserLinks(prevUserLinks => {
    return [...prevUserLinks, newUserLink];
  });
  props.setShowNewLinkModal(false);
};


  return (
    <Modal show={true} onHide={() => props.setShowSettings(false)} animation={false}>
    <Modal.Header closeButton>
      <Modal.Title>Settings</Modal.Title>
    </Modal.Header>
    <Modal.Body>
    <input className="change-link-background" type="text" placeholder="Link Background Color" onChange={handleLinkBackgroundColor} />
    <input className="change-link-color" type="text" placeholder="Link Text Color" onChange={handleLinkColor}/>
    <div className="link-corner-styling-selection">
      <input type="radio" id="rounded-option" name="rounded-option" value="round" />
      <label for="rounded-option">Rounded Corners</label>
      <input type="radio" id="straight-option" name="straight-option" value="straight"/>
      <label for="straight-option">Straight Corners</label> 
    </div>
     <button className="add-link-btn">Save</button>
    </Modal.Body>
  </Modal>
  );
}

SettingsModal.propTypes = {
  linkBackgroundColor: PropTypes.string.isRequired,
  linkColor: PropTypes.string.isRequired,
  setShowSettings: PropTypes.func.isRequired
};

export default SettingsModal;
