/**
 * Editable link
 *
 * @copyright 2021 Adam Carlson - All rights reserved
 */

 import PropTypes from 'prop-types';
import './styles/editable_link.scss';
import React, {useState} from 'react';

 function EditableLink(props) {

  const handleDeleteLink = (linkId) => {
    props.setUserLinks((prevUserLinks) => {
      let updatedUserLinks = [...prevUserLinks];
      const index = updatedUserLinks.findIndex(userLink => userLink.id === linkId);
      if (index == -1) {
        return updatedUserLinks;
      }
      updatedUserLinks.splice(index, 1);
      return updatedUserLinks;
    }); 

    const requestOptions = {
      method: 'DELETE'
    };
    const url = `https://retoolapi.dev/lqtPSO/links/${linkId}`;
    fetch(url, requestOptions);
  };

  const handleEditLink = (linkId) => {
    const index = props.userLinks.findIndex(userLink => userLink.id === linkId);
    if (index == -1) {
      return;
    }

    setEditLink({...props.userLinks[index]});
    props.setShowEditLinkModal(true);
  };

   return (
    <li className="editable-link-item">
      <span className="editable-link-title">Link Title: {props.linkTitle}</span>
      <span className="editable-link-url">Link Url: {props.linkUrl}</span>
      <span className="editable-link-clicks">Clicks: {props.linkClicks}</span>
      <span className="edit-link-btn" onClick={() => {handleEditLink(props.linkId)}}>&#9998;</span>
      <span className="delete-link-btn" onClick={() => {handleDeleteLink(props.linkId)}}>&#x274C;</span>
    </li>
   );
 }

EditableLink.propTypes = {
  linkId: PropTypes.number.isRequired,
  linkTitle: PropTypes.string.isRequired,
  linkUrl: PropTypes.string.isRequired,
  linkClicks: PropTypes.number.isRequired,
  setUserLinks: PropTypes.func.isRequired,
  setShowEditLinkModal: PropTypes.func.isRequired
};

 export default EditableLink;
